mapper.select:
    select 

    from user


	       and id = #{ id,jdbcType=BIGINT }


	       and `name` = #{ name,jdbcType=VARCHAR }


	       and `password` = #{ password,jdbcType=VARCHAR }


	       and `status` = #{ status,jdbcType=TINYINT }


	       and ctime = #{ ctime,jdbcType=BIGINT }


	       and utime = #{ utime,jdbcType=BIGINT }

    select count(1) from user 


	       and id = #{ id,jdbcType=BIGINT }


	       and `name` = #{ name,jdbcType=VARCHAR }


	       and `password` = #{ password,jdbcType=VARCHAR }


	       and `status` = #{ status,jdbcType=TINYINT }


	       and ctime = #{ ctime,jdbcType=BIGINT }


	       and utime = #{ utime,jdbcType=BIGINT }

    select 

    from user



	           and id = #{ queryParam.id,jdbcType=BIGINT }


	           and `name` = #{ queryParam.name,jdbcType=VARCHAR }


	           and `password` = #{ queryParam.password,jdbcType=VARCHAR }


	           and `status` = #{ queryParam.status,jdbcType=TINYINT }


	           and ctime = #{ queryParam.ctime,jdbcType=BIGINT }


	           and utime = #{ queryParam.utime,jdbcType=BIGINT }



	limit
	#{offset},#{rows}

mapper.delete:
update user set status = 0 
where 
	id = #{ id,jdbcType=BIGINT }
